# 1. 랜덤 날짜/시간 생성
def random_date_between(start_date: date, end_date: date) -> date:
    """두 날짜 사이의 랜덤 날짜 반환 (inclusive)"""
    time_between = end_date - start_date
    days_between = time_between.days
    if days_between < 0:
        raise ValueError("종료일은 시작일 이후여야 합니다.")
    random_days = random.randint(0, days_between)
    return start_date + timedelta(days=random_days)

# 2. 랜덤 datetime 생성
def random_datetime_between(start_datetime: datetime, end_datetime: datetime) -> datetime:
    """두 datetime 사이의 랜덤 datetime 반환"""
    time_between = end_datetime - start_datetime
    seconds_between = time_between.total_seconds()
    if seconds_between <= 0:
        raise ValueError("종료일시는 시작일시 이후여야 합니다.")
    random_seconds = random.randint(0, int(seconds_between))
    return start_datetime + timedelta(seconds=random_seconds)

# 3. 가중치 기반 랜덤 선택
def weighted_choice(choices: List[Any], weights: List[float]) -> Any:
    """가중치 기반 랜덤 선택"""
    return random.choices(choices, weights=weights, k=1)[0]

# 4. 정규분포 기반 정수 생성
def gaussian_int(mean: float, std: float, min_val: int, max_val: int) -> int:
    """정규분포 기반 정수 반환 (범위 제한)"""
    value = random.gauss(mean, std)
    return max(min_val, min(max_val, int(round(value))))

# 5. 중복되지 않는 username/email 생성
def generate_unique_username(existing_usernames: Set[str]) -> str:
    """
    중복되지 않는 username 생성
    """
    max_attempts = 100
    for _ in range(max_attempts):
        username = f"{fake.user_name()}{random.randint(100, 9999)}"
        if username not in existing_usernames:
            return username
    # fallback
    return f"user_{fake.uuid4()[:8]}"

# 6. 중복되지 않는 email 생성
def generate_unique_email(existing_emails: Set[str]) -> str:
    """
    중복되지 않는 email 생성
    """
    max_attempts = 100
    for _ in range(max_attempts):
        email = fake.email()
        if email not in existing_emails:
            return email
    # fallback
    return f"user_{fake.uuid4()[:8]}@example.com"
